using UnityEngine;using System.Collections;using UnityEngine.UI;public class TextureToSpriteClass : MonoBehaviour{	/*-----------------------------------------------	 * convert a Texture2D object into a Sprite object	 */	private Sprite TextureToSprite(Texture2D texture)	{		// get the size of the texture		Rect rect = new Rect(0, 0, texture.width, texture.height);		// set pivot point to be the center 0.5 = half way		Vector2 pivot = new Vector2(0.5f, 0.5f);		// create new Sprite object with out texture, size and pivot		Sprite sprite = Sprite.Create(texture, rect, pivot);		return sprite;	}}